import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-view-card',
  templateUrl: './view-card.component.html',
  styleUrls: ['./view-card.component.css'],
})
export class ViewCardComponent implements OnInit {

  constructor(
    private route: ActivatedRoute() {

    }
  )

  @Input()
  imageViewCard: string = '';

  @Input()
  titleViewCard: string = '';

  @Input()
  contentViewCard: string = '';

  @Input()
  authorNameViewCard: string = '';

  @Input()
  authorImageViewCard: string = '';

  @Input()
  dateViewCard: string = '';

  ngOnInit(): void {}
}
<div class="conteiner-card">
  <div class="card-image">
    <img src="{{ imageViewCard }}" alt="imagepost" />
  </div>
  <div class="card-title">
    <h2>{{ titleViewCard }}</h2>
  </div>
  <div class="card-content">
    <p>{{ contentViewCard }}</p>
  </div>
  <div class="card-author">
    <img src="{{ authorImageViewCard }}" alt="img author" />
    <span>{{ authorNameViewCard }}</span>
    <span>{{ dateViewCard }}</span>
  </div>
</div>
