import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-small-cards',
  templateUrl: './small-cards.component.html',
  styleUrls: ['./small-cards.component.css'],
})
export class SmallCardsComponent implements OnInit {
  ngOnInit(): void {}

  @Input()
  id: string = '';

  @Input()
  image_post: string = '';

  @Input()
  title_post: string = '';

  @Input()
  content_post: string = '';

  @Input()
  author_name: string = '';

  @Input()
  author_image: string =
    'https://upload.wikimedia.org/wikipedia/commons/thumb/2/25/Mark_Twain%2C_Brady-Handy_photo_portrait%2C_Feb_7%2C_1871%2C_cropped.jpg/150px-Mark_Twain%2C_Brady-Handy_photo_portrait%2C_Feb_7%2C_1871%2C_cropped.jpg';

  @Input()
  post_date: string = '';

  @Input()
  link_redirect: string = '';

  arrlinks: Array<string> = [
    'https://news.ifood.com.br/ifood-faz-campanha-de-doacao-para-formar-mulheres-em-tecnologia/',
    'https://news.ifood.com.br/ifood-investe-r-12-milhoes-na-formacao-em-tecnologia/',
    'https://news.ifood.com.br/como-evitar-acidentes-10-dicas-de-direcao-defensiva/',
    'https://news.ifood.com.br/ifood-oferece-curso-de-direcao-defensiva-para-entregadores/',
    'https://news.ifood.com.br/entregadores-contam-educacao-mudando-vidas/',
    'https://news.ifood.com.br/vocacao-capacita-jovens-para-desenvolver-seus-projetos-de-vida/',
    'https://news.ifood.com.br/como-fazer-doacao-no-app-do-ifood/',
    'https://news.ifood.com.br/potencia-tech-promove-hackathon-online-em-maio/',
    '',
  ];
}
<li class="small-cards" title="card of list">
  <div><img src="{{ image_post }}" alt="image-small-card" /></div>
  <h2 class="title-small-card">
    <a *ngFor="let link of arrlinks" href="{{ link }}">{{ title_post }}</a>
  </h2>
  <p class="content-small-card">{{ content_post }}</p>
  <span class="author-small-card">
    <div><img src="{{ author_image }}" alt="image author" /></div>
    <h3>{{ author_name }}</h3>
    <span>{{ post_date }}</span> </span
  >s
  <a [routerLink]="['viewCard', id]">Visualizar post</a>
</li>
