import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-small-cards',
  templateUrl: './small-cards.component.html',
  styleUrls: ['./small-cards.component.css'],
})
export class SmallCardsComponent implements OnInit {
  ngOnInit(): void {
     @Input()
  image_post: string = 'Imagem dinamica 5';

  @Input()
  title_post: string = 'Titulo dinamico 5';

  @Input()
  content_post: string = 'C5';

  @Input()
  author_name: string = 'A5';

  @Input()
  author_image: string = 'A5';

  @Input()
  post_date: string = 'PD5';
}
  }
  myFn() {
    alert('hello');
  }


<li class="small-cards" title="card of list">
  <div><img [src]="image_post" alt="image-small-card" /></div>
  <h2 class="title-small-card">{{ title_post }}</h2>
  <p class="content-small-card">{{ content_post }}</p>
  <span class="author-small-card">
    <div><img [src]="author_image" alt="image author" /></div>
    <h3>{{ author_name }}</h3>
    <span>{{ post_date }}</span>
  </span>
  <button (click)="myFn()">Ver</button>
</li>
