import { Component } from '@angular/core';

@Component({
  selector: 'app-small-cards',
  templateUrl: './small-cards.component.html',
  styleUrls: ['./small-cards.component.css']
})
export class SmallCardsComponent {

}
<li class="small-cards" title="card of list">
  <div><img src="" alt="image-small-card" /></div>
  <h2 class="title-small-card">Title small card</h2>
  <p class="content-small-card">Content small card</p>
  <span class="author-small-card">
    <div><img src="" alt="image author" /></div>
    <h3>Name author</h3>
    <span>Date 19 jan 2023</span>
  </span>
  <button>Ver</button>
</li>
