import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent implements OnInit {
  ngOnInit(): void {}

  logoHeader: string = '';
}
<div class="header">
  <div class="logo-ifood"><img src="{{ logoHeader }}" alt="" /></div>
  <app-menu class="menu">Menu</app-menu>
</div>
